source.isChannelUrl = function (url: string): boolean {
  const urlLower = url.toLowerCase();
  return urlLower.includes('{{PLATFORM_HOSTNAME}}') && 
         (urlLower.includes('channel') || urlLower.includes('user') || urlLower.includes('@'));
};

